import React from "react";
import { useSelector, useDispatch } from "react-redux";
import {
  toggleProductSelection,
  removeProductFromBasket,
  removeFromBasket,
  addToBasket,
} from "../ReduxToolkit/addToBasketReducer";
import { Badge, Container, Row } from "react-bootstrap";
import Button from "react-bootstrap/Button";
import Card from "react-bootstrap/Card";
import { Form } from "react-router-dom";
import Image from "react-bootstrap/Image";

const BasketItems = () => {
  const dispatch = useDispatch();
  const addedProducts = useSelector((state) => state.addToBasket.addedProducts);
  const handleProductCheckbox = (productId) => {
    dispatch(toggleProductSelection(productId));
  };
  const handleRemoveProductFromBasket = (productId) => {
    dispatch(removeProductFromBasket(productId));
  };
  const handleRemoveFromBasket = (productId) => {
    dispatch(removeFromBasket(productId));
  };
  const handleAddToBasket = (product) => {
    dispatch(addToBasket(product));
  };

  return (
    <>
      <Container>
        {addedProducts.map((product) => (
          <Card key={product.id}>
            <Card.Header>
              <Form>
                <Form.Check
                  type="checkbox"
                  name={product.name}
                  value={product.name}
                  checked={product.isSelected}
                  onChange={() => handleProductCheckbox(product.id)}
                />
              </Form>
              <hr />
            </Card.Header>
            <Card.Body>
              <Row>
                <Image src={product.image}></Image>
                <Card.Title>
                  <h3>{product.title}</h3>
                  <h6>{product.category}</h6>
                </Card.Title>
                <Card.Text>
                  <h3>Price: ${product.price}</h3>
                  <p>{product.rating.rate}</p>
                  <Button
                    className="bg-body-danger"
                    onClick={() => handleRemoveProductFromBasket(product.id)}
                  >
                    Remove
                  </Button>
                </Card.Text>
                <Card.Text>
                  <Badge className="d-flex p-2 bg-body-secondary">
                    <Button onClick={() => handleRemoveFromBasket(product.id)}>
                      -
                    </Button>
                    <span>{product.quantity}</span>
                    <Button onClick={() => handleAddToBasket(product)}>
                      +
                    </Button>
                  </Badge>
                </Card.Text>
                <Button variant="primary">Go somewhere</Button>
              </Row>
            </Card.Body>
          </Card>
        ))}
      </Container>
    </>
  );
};

export default BasketItems;
