import React from "react";
import { Container } from "react-bootstrap";
import Tab from "react-bootstrap/Tab";
import Tabs from "react-bootstrap/Tabs";
import ProductListing from "./ProductListing";
import { useDispatch } from "react-redux";
import { filterProductsByCategory } from "../ReduxToolkit/productReducer";

const Categories = () => {
  const dispatch = useDispatch();
  const handleTabSelect = (category) => {
    dispatch(filterProductsByCategory(category));
  };

  return (
    <Container className="mt-3">
      <h1>Categories</h1>
      <hr />
      <Tabs
        defaultActiveKey="profile"
        id="fill-tab-example"
        className="mb-3"
        fill
      >
        <Tab eventKey="All" title="All">
          <ProductListing />
        </Tab>
        <Tab
          eventKey="Electronics"
          title="Electronics"
          onSelect={() => handleTabSelect("electronics")}
        >
          <ProductListing />
        </Tab>
        <Tab eventKey="Women's Clothing" title="Women's Clothing">
          Women's Clothing{" "}
        </Tab>
        <Tab eventKey="Men's Clothing" title="Men's Clothing">
          Men's Clothing{" "}
        </Tab>
        <Tab eventKey="Jewelery" title="Jewelery">
          Jewelery{" "}
        </Tab>
      </Tabs>
    </Container>
  );
};

export default Categories;
