import React, { useState } from "react";
import ProductListing from "./ProductListing";
import "../Styles/searchResults.css";
import Header from "./Header";
import { useDispatch } from "react-redux";
import { filterProductsByCategory } from "../Redux-Toolkit/productReducer";
import { useSelector } from "react-redux";

const SearchResults = () => {
  const dispatch = useDispatch();
  const [selectedCategory, setSelectedCategory] = useState("");

  const handleCategoryFilter = (category) => {
    setSelectedCategory(category);
    dispatch(filterProductsByCategory(category)); 
  };
  const filteredProducts = useSelector(
    (state) => state.product.filteredProducts
  );
  const searchTerm = useSelector((state) => state.product.searchTerm);

  return (
    <>
      <Header />
      <div className="SearchResults">
        <h2>Home - Search Results</h2>
        <hr />
        <div className="con_header">
          <h2>
            {filteredProducts.length} Items Found for "{searchTerm}‚Äù
          </h2>
          <div className="filter">
            <form>
              <label htmlFor="products">Sort By:</label>
              <select
                id="products"
                name="products"
                onChange={(e) => handleCategoryFilter(e.target.value)}
                value={selectedCategory}
              >
                <option value="all">All</option>
                <option value="electronics">Electronics</option>
                <option value="women's clothing">Women's clothing</option>
                <option value="men's clothing">Men's clothing</option>
                <option value="jewelery">Jewelery</option>
              </select>
            </form>
          </div>
        </div>
        <hr />
        <div className="SearchResults_container">
          <ProductListing selectedCategory={selectedCategory} />
        </div>
      </div>
    </>
  );
};

export default SearchResults;
