import React from "react";
import Header from "../Components/Header";
import HeaderBasket from "../Components/HeaderBasket";
import BasketContainer from "../Components/BasketContainer";
import { useSelector, useDispatch } from "react-redux";
import { useNavigate } from "react-router-dom";
import { setBoughtStatus } from "../ReduxToolkit/buyNowReducer";
import { resetAddedProductsSlice } from "../ReduxToolkit/addToBasketReducer";
import Offcanvas from "react-bootstrap/Offcanvas";


const options = [
    {
      name: "Enable both scrolling & backdrop",
      scroll: true,
      backdrop: true,
    },
  ];

const Basket = () => {
  const isBought = useSelector((state) => state.buyNow.isBought);
  const dispatch = useDispatch();
  const navigate = useNavigate();
  const handleNavigate = () => {
    // navigate("/orders");
    dispatch(setBoughtStatus(false));
    // dispatch(resetAddedProductsSlice());
  };

  
  const [show, setShow] = useState(false);

  const handleClose = () => setShow(false);
  const toggleShow = () => setShow((s) => !s);


  return (
    <>
      {isBought && (
        <Offcanvas show={isBought} onHide={true}>
          <Offcanvas.Header>
            <Offcanvas.Title>Order Confirmation</Offcanvas.Title>
          </Offcanvas.Header>
          <Offcanvas.Body>
            <p>Your order has been placed successfully.</p>
            <p>Thank you for shopping with us!</p>
            <button onClick={handleNavigate}>View Orders</button>
          </Offcanvas.Body>
        </Offcanvas>
      )}
      <Header />
      <HeaderBasket />
      <BasketContainer />
    </>
  );
};

export default Basket;
