import React, { useEffect } from "react";
import axios from "axios";
import { useParams, useNavigate } from "react-router-dom";
import { useSelector, useDispatch } from "react-redux";
import { addToBasket } from "../ReduxToolkit/addToBasketReducer";
import { removeFromBasket } from "../ReduxToolkit/addToBasketReducer";
import { removeSelectedProducts } from "../ReduxToolkit/addToBasketReducer";
import {selectedProduct} from "../ReduxToolkit/"

import Header from "../Components/Header";
import {
  Button,
  Container,
  Row,
  Col,
  Image,
  Badge,
  Card,
  Form,
} from "react-bootstrap";

const ProductDetails = () => {
  const { productId } = useParams();
  const product = useSelector((state) =>
    state.product.products.find((p) => p.id === parseInt(productId))
  );
  const dispatch = useDispatch();

  const handleAddToBasket = () => {
    dispatch(addToBasket(product));
  };

  const handleRemoveFromBasket = () => {
    dispatch(removeFromBasket(product.id));
  };

  const navigate = useNavigate();
  const handleNavigate = () => {
    navigate("/basket");
  };

  const productInBasket = useSelector((state) =>
    state.addToBasket.addedProducts.find((item) => item.id === product.id)
  );
  const quantity = productInBasket ? productInBasket.quantity : 0;

  useEffect(() => {
    const fetchProductDetail = async (id) => {
      const response = await axios
        .get(`https://fakestoreapi.com/products/${id}`)
        .catch((err) => {
          console.log("Err: ", err);
        });
      dispatch(selectedProduct(response.data));
    };

    if (productId && productId !== "") fetchProductDetail(productId);

    return () => {
      dispatch(removeSelectedProduct());
    };
  }, [productId, dispatch]);

  const { image, title, price, category, rating } = product;

  return (
    <Container>
      <Header />
      {Object.keys(product).length === 0 ? (
        <Row className="Loading">
          <Col>
            <h2>...Loading</h2>
          </Col>
        </Row>
      ) : (
        <>
          <Row className="path_bar">
            <Col>
              <h2>Home - Search - ProductDetails - {title}</h2>
            </Col>
            <Col className="icons">
              <Image
                src="https://example.com/path-to-your-icon.svg"
                alt="Your icon"
                width={24}
                height={19}
              />
              <Image
                src="https://example.com/path-to-your-icon.svg"
                alt="Your icon"
                width={20}
                height={20}
              />
            </Col>
          </Row>
          <Row className="container_product">
            <Col className="product_cart">
              <div className="product_cart_container">
                <Row className="info">
                  <Col xs={12} md={6} className="img_box">
                    <Image src={image} alt={title} fluid />
                  </Col>
                  <Col xs={12} md={6} className="content_product">
                    <h1>{title}</h1>
                    <Row className="product_rating">
                      <Col>
                        <h4>⭐️{rating.rate}</h4>
                      </Col>
                      <Col>
                        <h5>{rating.count} left</h5>
                      </Col>
                    </Row>
                    <h2>${price}</h2>
                    <p>{category}</p>
                    <Row className="qty">
                      <Col xs={12} md={6}>
                        <h1>Quantity</h1>
                      </Col>
                      <Col xs={12} md={6}>
                        <Row className="qty_change">
                          <Col>
                            <Button onClick={handleRemoveFromBasket}>-</Button>
                          </Col>
                          <Col>
                            <span>{quantity}</span>
                          </Col>
                          <Col>
                            <Button onClick={handleAddToBasket}>+</Button>
                          </Col>
                        </Row>
                      </Col>
                    </Row>
                    <Row className="add_buttons">
                      <Col>
                        {/* <Button className="buy" onClick={handleBuyNow} >Buy Now</Button> */}
                      </Col>
                      <Col>
                        <Button className="add" onClick={handleNavigate}>
                          Add To Basket
                        </Button>
                      </Col>
                    </Row>
                  </Col>
                </Row>
                <Row className="additional_info">
                  <Col xs={12} md={4} className="info_col">
                    <h6>Sold By:</h6>
                    <h1>NIKE company</h1>
                  </Col>
                  <Col xs={12} md={4} className="info_col">
                    <h6>Services:</h6>
                    <pre>-7 Days Return</pre>
                    <pre>-Warranty not available</pre>
                  </Col>
                  <Col xs={12} md={4} className="info_col">
                    <h6>Cash on Delivery</h6>
                    <pre>Standard Delivery : 1-3 jan</pre>
                    <pre>Lahore, Pakistan</pre>
                  </Col>
                </Row>
              </div>
            </Col>
          </Row>
        </>
      )}
    </Container>
  );
};

export default ProductDetails;
